import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FiHeart, FiMessageCircle, FiShare2, FiBookmark, FiMoreHorizontal } from 'react-icons/fi';
import './Post.css';

const Post = ({ post }) => {
  const [isLiked, setIsLiked] = useState(post.isLiked || false);
  const [isSaved, setIsSaved] = useState(post.isSaved || false);
  const [likesCount, setLikesCount] = useState(post.likes || 0);

  const handleLike = () => {
    if (isLiked) {
      setLikesCount(likesCount - 1);
    } else {
      setLikesCount(likesCount + 1);
    }
    setIsLiked(!isLiked);
  };

  const handleSave = () => {
    setIsSaved(!isSaved);
  };

  return (
    <div className="post">
      <div className="post-image-container">
        <Link to={`/post/${post.id}`}>
          <img 
            src={post.imageUrl} 
            alt={post.title}
            className="post-image"
            loading="lazy"
          />
        </Link>
        
        {/* Save Button */}
        <button 
          className={`save-btn ${isSaved ? 'saved' : ''}`}
          onClick={handleSave}
          aria-label={isSaved ? 'Remove from saved' : 'Save post'}
        >
          <FiBookmark />
        </button>

        {/* More Options */}
        <button className="more-btn" aria-label="More options">
          <FiMoreHorizontal />
        </button>
      </div>

      <div className="post-content">
        <h3 className="post-title">
          <Link to={`/post/${post.id}`}>{post.title}</Link>
        </h3>
        
        <p className="post-description">{post.description}</p>
        
        <div className="post-meta">
          <div className="post-author">
            <img 
              src={post.author.avatar} 
              alt={post.author.name}
              className="author-avatar"
            />
            <span className="author-name">{post.author.name}</span>
          </div>
          
          <div className="post-stats">
            <button 
              className={`stat-btn ${isLiked ? 'liked' : ''}`}
              onClick={handleLike}
              aria-label={isLiked ? 'Unlike' : 'Like'}
            >
              <FiHeart />
              <span>{likesCount}</span>
            </button>
            
            <Link to={`/post/${post.id}`} className="stat-btn">
              <FiMessageCircle />
              <span>{post.comments || 0}</span>
            </Link>
            
            <button className="stat-btn" aria-label="Share">
              <FiShare2 />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Post;
